  },
  methods: {
    // 打开摄像头
    openPhoto: function openPhoto() {
      var _this2 = this;

      navigator.mediaDevices.getUserMedia({
        audio: true,
        video: {
          width: 720,
          height: 720
        }
      }).then(function (success) {
        // 摄像头开启成功
        _this2.$refs["video"].srcObject = success; //srcObject 是实时流
        // 实时拍照效果

        _this2.$refs["video"].play();
      }).catch(function (error) {
        //摄像头开启失败
        console.error("摄像头开启失败");
      });

      var _this = this;

      setTimeout(function () {
        _this.photograph();
      }, 2000);
    },
    base64toFile: function base64toFile(dataBase64) {
      var filename = arguments.length > 1 && arguments[1] !== undefined ? arguments[1] : 'file';
      var arr = dataBase64.split(',');
      var mime = arr[0].match(/:(.*?);/)[1]; //设置file⽂件流的type名称

      var suffix = mime.split('/')[1]; //设置file⽂件流的name名称

      var bstr = window.atob(arr[1]);
      var n = bstr.length;
      var u8arr = new Uint8Array(n);

      while (n--) {
        u8arr[n] = bstr.charCodeAt(n);
      }

      return new File([u8arr], "".concat(filename, ".").concat(suffix), {
        type: mime
      });
    },
    // 拍照
    photograph: function photograph() {
      var photoInfo = this.$refs["canvas"].getContext("2d"); // 把当前内容渲染到canvas上

      photoInfo.drawImage(this.$refs["video"], 0, 0, 480, 480); // //canvas图片 转base64格式、图片格式转换、图片质量压缩

      var img = this.$refs["canvas"].toDataURL("image/jpg");
      var imgBase64 = this.$refs["canvas"].toDataURL("image/jpg", 0.7);
      var string = imgBase64.replace("data:image/jpg;base64,", "");
      string = this.base64toFile(imgBase64, 'camera');
      console.log(string);
      var formData = new FormData();
      console.log(formData);
      formData.append('image', string);
      var url = 'https://xiaoku.store:5000/recognition';
      axios.post(url, formData).then(function (res) {
        console.log(res.data);
      }).catch(function (err) {
        alert(err);
      });
    }
  }
