import Aside from "@/components/Aside";
import Header from "@/components/Header"; // import request from "@/utils/request";

export default {
  name: 'HomeView',
  components: {
    Aside: Aside,
    Header: Header
  },
  created: function created() {
    // 从后台获取最新的User数据
    this.getUser();
  },
  data: function data() {
    return {
      sideWidth: 200
    };
  },
  methods: {
    getUser: function getUser() {
      var _this = this;

      var username = localStorage.getItem("user") ? JSON.parse(localStorage.getItem("user")).username : "";

      if (username) {
        // 从后台获取User数据
        this.request.get("/user/username/" + username).then(function (res) {
          // 重新赋值后台的最新User数据
          _this.user = res.data;
        });
      }
    }
  }
};
