import Head from "@/components/Head";
import Map from "@/views/Map";
import moment from "moment";
import axios from 'axios';
import theMeeting from "@/views/meeting/index";
export default {
  name: "AddMeeting",
  components: {
    Map,
    Head,
    theMeeting
  },
  children: [{
    name: 'Map',
    path: 'Map',
    components: Map
  }],

  data() {
    return {
      nowTime: new Date(),
      // 当前时间
      dialogTable: false,
      theface: false,
      meetingId: '',
      form: '',
      code: '',
      uname: '',
      faceIn: 1,
      user: localStorage.getItem("user") ? JSON.parse(localStorage.getItem("user")) : {}
    };
  },

  created() {
    this.enterSearch();
  },

  methods: {
    check() {},

    back() {
      this.meetingId = '';
      this.code = '';
    },

    sign() {
      let t1 = moment().startOf('millisecond').format("YYYY-MM-DD HH:mm:ss");
      console.log(this.form, "==================this.form====================");
      const form3 = {
        id: this.form.udid,
        meetingId: this.form.meetingId,
        participateStatus: this.form.participateStatus,
        userId: this.user.id
      };
      form3.signInDate = JSON.parse(JSON.stringify(t1)); // this.form.udid=form3.id

      let t2 = moment(this.form.startDate).format("YYYY-MM-DD HH:mm:ss");

      if (t2 >= t1) {
        form3.participateStatus = 1;
      }

      if (t2 < t1) {
        form3.participateStatus = 2;
      }

      form3.signAdress = localStorage.getItem("location");

      if (this.form.participateStatus === 0) {
        this.request.post("/AboutMeeting/manualSign", form3).then(res => {
          if (res.code === '200') {
            this.$message.success("签到成功");

            if (this.form.createUser === this.user.id) {
              const from1 = this.form;
              from1.id = this.form.meetingId;
              from1.stage = 1;
              from1.name = this.form.mname;
              from1.startDate = this.form.startDate.format("YYYY-MM-DD HH:mm:ss");
              this.request.post('/meeting/MyMeeting', from1);
              this.$router.push({
                name: "theMeeting",
                params: {
                  meetingName: this.form.mname,
                  meetingId: this.form.meetingId,
                  roomId: this.form.code,
                  udid: this.form.udid,
                  qwq: 1
                }
              });
            } else {
              this.$message.success("加入成功");
              this.$router.push({
                name: "theMeeting",
                params: {
                  meetingName: this.form.mname,
                  meetingId: this.form.meetingId,
                  udid: this.form.udid,
                  roomId: this.form.code
                }
              });
            } // this.form.checkStatus = 1

          } else {
            this.$message.error("签到失败");
          }
        });
      } else {
        this.$message.error("您已签到,请勿重复签到！");

        if (this.form.createUser === this.user.id) {
          const from1 = this.form;
          from1.id = this.form.meetingId;
          from1.stage = 1;
          from1.name = this.form.mname;
          from1.startDate = this.form.startDate.format("YYYY-MM-DD HH:mm:ss");
          this.request.post('/meeting/MyMeeting', from1).then(res => {
            console.log(res);
          });
          this.$message.success("创建成功1号");
          this.$router.push({
            name: "theMeeting",
            params: {
              meetingName: this.form.mname,
              meetingId: this.form.meetingId,
              roomId: this.form.code,
              udid: this.form.udid,
              qwq: 1
            }
          });
        } else {
          this.$message.success("加入成功");
          this.$router.push({
            name: "theMeeting",
            params: {
              meetingName: this.form.mname,
              meetingId: this.form.meetingId,
              udid: this.form.udid,
              roomId: this.form.code
            }
          });
        }
      }
    },

    join() {
      this.request.get("/AboutMeeting/join", {
        params: {
          code: this.code,
          userId: this.user.id
        }
      }).then(res => {
        if (res.code === '200') {
          this.form = res.data;
          console.log(res.data);
          this.meetingId = res.data.id;

          if (res.data.mname === undefined) {
            this.form.mname = res.data.name;
            this.form.checkStatus = 0;
          }

          if (res.data.checkStatus === 0) {
            this.$confirm('是否报名此次会议？', '您还未报名此会议', {
              confirmButtonText: '确定',
              cancelButtonText: '取消',
              type: 'warning',
              center: true
            }).then(() => {
              const from1 = {
                // meetingId: res.data.meetingId,
                checkStatus: 1,
                userId: this.user.id
              };

              if (res.data.udid !== undefined) {
                from1.id = res.data.udid;
              }

              if (res.data.meetingId !== undefined) {
                from1.meetingId = res.data.meetingId;
              }

              if (res.data.meetingId === undefined) {
                from1.meetingId = res.data.id;
              }

              this.request.post('/AboutMeeting', from1).then(res => {
                if (res.code === '200') {
                  this.$message.success("报名成功");
                  this.form.checkStatus = 1;
                  res.checkStatus = 1;
                } else {
                  this.$message.error("报名失败");
                }
              });
            }).catch(() => {
              this.$message({
                type: 'info',
                message: '已取消报名'
              });
            });
          }
        } else {
          this.$message.error("会议号有误！");
        }
      });
    },

    cback() {
      this.dialogTable = false;
    },

    joinMeeting() {
      if (this.form.status === 0 && this.form.stage !== 2) {
        if (this.form.createUser === this.user.id) {
          this.$message.success("加入成功");
          localStorage.setItem("meeting", JSON.stringify(this.form)); // 存储用户信息到浏览器

          this.$router.push("/AllSign");
        } else {
          this.$message.success("您无权操作！");
        }
      } else if (this.form.stage === 2) {
        this.$message({
          showClose: true,
          //message: res.msg,
          type: 'error',
          message: '该会议已结束'
        });
      } else {
        if (this.form.createUser === this.user.id) {
          if (this.faceIn === 1) {
            this.theface = true;
            this.openPhoto();
            console.log(this.faceIn);
          }

          if (this.faceIn === 0) {
            this.dialogTable = true;
            this.faceIn = 1;
          }
        } else {
          if (this.form.stage === 1) {
            if (this.faceIn === 1) {
              this.theface = true;
              this.openPhoto();
              console.log(this.faceIn);
            }

            if (this.faceIn === 0) {
              this.dialogTable = true;
              this.faceIn = 1;
            }
          } else {
            this.$message({
              showClose: true,
              //message: res.msg,
              type: 'warning',
              message: '会议还未开始！'
            });
          }
        }
      }
    },

    enterSearch() {
      document.onkeydown = e => {
        //13表⽰回车键，baseURI是当前页⾯的地址，为了更严谨，也可以加别的，可以打印e看⼀下
        if (e.keyCode === 13 && e.target.baseURI.match(/AddMeeting/)) {
          //回车后执⾏搜索⽅法
          this.join();
        }
      };
    },

    //人脸识别
    openPhoto() {
      navigator.mediaDevices.getUserMedia({
        audio: true,
        video: {
          width: 720,
          height: 720
        }
      }).then(success => {
        // 摄像头开启成功
        this.$refs["video"].srcObject = success; //srcObject 是实时流
        // 实时拍照效果

        this.$refs["video"].play();

        let _this = this;

        setTimeout(function () {
          _this.photograph();
        }, 1140);
      }).catch(() => {
        //摄像头开启失败
        alert("摄像头开启失败,即将开启手动签到");
        this.dialogTable = true;
        this.faceIn = 1;
      });
    },

    base64toFile(dataBase64, filename = 'file') {
      let arr = dataBase64.split(',');
      let mime = arr[0].match(/:(.*?);/)[1]; //设置file⽂件流的type名称

      let suffix = mime.split('/')[1]; //设置file⽂件流的name名称

      const bstr = window.atob(arr[1]);
      let n = bstr.length;
      const u8arr = new Uint8Array(n);

      while (n--) {
        u8arr[n] = bstr.charCodeAt(n);
      }

      return new File([u8arr], `${filename}.${suffix}`, {
        type: mime
      });
    },

    // 拍照
    photograph() {
      let photoInfo = this.$refs["canvas"].getContext("2d"); // 把当前内容渲染到canvas上

      photoInfo.drawImage(this.$refs["video"], 0, 0, 480, 480); // //canvas图片 转base64格式、图片格式转换、图片质量压缩

      let img = this.$refs["canvas"].toDataURL("image/jpg");
      let imgBase64 = this.$refs["canvas"].toDataURL("image/jpg", 0.7);
      let string = imgBase64.replace("data:image/jpg;base64,", "");
      string = this.base64toFile(imgBase64, 'camera');
      console.log(string);
      let formData = new FormData();
      console.log(formData);
      formData.append('image', string);
      var url = 'https://xiaoku.store:5000/recognition';
      axios.post(url, formData).then(res => {
        console.log(res.data.results[0].username); //人脸识别成功

        let that = this;

        if (res.data.results[0].username === this.user.name) {
          //签到信息
          let t1 = moment().startOf('millisecond').format("YYYY-MM-DD HH:mm:ss");
          const form3 = {
            id: that.form.udid,
            meetingId: that.form.meetingId,
            participateStatus: that.form.participateStatus,
            userId: that.user.id
          };
          form3.signInDate = JSON.parse(JSON.stringify(t1)); // this.form.udid=form3.id

          let t2 = moment(that.form.startDate).format("YYYY-MM-DD HH:mm:ss");

          if (t2 >= t1) {
            form3.participateStatus = 1;
          }

          if (t2 < t1) {
            form3.participateStatus = 2;
          }

          if (that.form.participateStatus === 0) {
            that.request.post("/AboutMeeting/manualSign", form3).then(res => {
              if (res.code === '200') {
                if (that.form.createUser === that.user.id) {
                  const from1 = that.form;
                  from1.id = that.form.meetingId;
                  from1.stage = 1;
                  from1.name = that.form.mname;
                  from1.startDate = that.form.startDate.format("YYYY-MM-DD HH:mm:ss"); // 修改

                  that.request.post('/meeting/MyMeeting', from1).then(that.$message.success("创建成功2号"));
                  that.$router.push({
                    name: "theMeeting",
                    params: {
                      meetingName: that.form.mname,
                      meetingId: that.form.meetingId,
                      roomId: that.form.code,
                      udid: that.form.udid,
                      qwq: 1
                    }
                  });
                } else {
                  that.$message.success("加入成功");
                  that.$router.push({
                    name: "theMeeting",
                    params: {
                      meetingName: that.form.mname,
                      meetingId: that.form.meetingId,
                      udid: that.form.udid,
                      roomId: that.form.code
                    }
                  });
                }
              }
            });
          } else {
            that.$message.error("您已签到,请勿重复签到！");

            if (that.form.createUser === that.user.id) {
              const from1 = that.form;
              from1.id = that.form.meetingId;
              from1.stage = 1;
              from1.name = that.form.mname;
              from1.startDate = that.form.startDate.format("YYYY-MM-DD HH:mm:ss");
              that.request.post('/meeting/MyMeeting', from1);
              that.$message.success("创建成功3号");
              that.$router.push({
                name: "theMeeting",
                params: {
                  meetingName: that.form.mname,
                  meetingId: that.form.meetingId,
                  roomId: that.form.code,
                  udid: that.form.udid,
                  qwq: 1
                }
              });
            } else {
              that.$message.success("加入成功!");
              that.$router.push({
                name: "theMeeting",
                params: {
                  meetingName: that.form.mname,
                  meetingId: that.form.meetingId,
                  roomId: that.form.code,
                  udid: that.form.udid
                }
              });
            }
          }
        } else {
          alert("人脸识别失败,即将进行手动签到");
          that.$message.error("识别失败！即将进行手动签到");
          that.faceIn = 0;

          if (that.faceIn === 0) {
            that.dialogTable = true;
            that.faceIn = 1;
          }
        }
      }).catch(err => {
        alert("人脸识别失败,即将进行手动签到");
        this.$message.error("识别失败！即将进行手动签到");
        this.faceIn = 0;

        if (this.faceIn === 0) {
          this.dialogTable = true;
          this.faceIn = 1;
        }
      });
    }

  }
};
