export default {
  name: "File",

  data() {
    return {
      name: '',
      qeq: [],
      multipleSelection: [],
      pageNum: 1,
      pageSize: 5,
      total: 0,
      delBtlStatu: true,
      nextMarker: '',
      svg: '',
      treeData: [],
      // 树形结构数据
      tableData: [],
      // 表格数据
      currentPath: [],
      // 当前路径
      breadcrumb: [],
      // 面包屑数据
      treeProps: {
        label: 'name',
        children: 'children',
        type: 'type',
        isOpen: false
      }
    };
  },

  created() {
    this.load();
  },

  methods: {
    load() {
      this.request.get("/service/cos/list", {
        params: {
          folder: '',
          nextMarker: this.nextMarker
        }
      }).then(res => {
        if (res.code === "200") {
          this.nextMarker = res.data.nextMarker;
          this.treeData = [res.data.list];
          console.log("treeData=======》", this.treeData);
          this.treeData[0].name = "小酷文件管理";
          this.updateTableData(this.treeData[0]);
        } else {
          this.$message.error("查找失败");
        }
      });
    },

    changeEnable(row) {
      this.request.post("/file/update", row).then(res => {
        if (res.code === '200') {
          this.$message.success("操作成功");
        }
      });
    },

    del(id) {
      this.request.delete("/file/" + id).then(res => {
        if (res.code === '200') {
          this.$message.success("删除成功");
          this.load();
        } else {
          this.$message.error("删除失败");
        }
      });
    },

    handleSelectionChange(val) {
      console.log(val);
      this.multipleSelection = val;
      this.delBtlStatu = val.length == 0;
    },

    delBatch() {
      let ids = this.multipleSelection.map(v => ({
        name: v.name,
        id: v.id
      })); // [{}, {}, {}] => [1,2,3]

      this.qeq = ids; // this.request.post("/file/del/batch", ids).then(res => {
      //   if (res.code === '200') {
      //     this.$message.success("批量删除成功")
      //     this.load()
      //   } else {
      //     this.$message.error("批量删除失败")
      //   }
      // })

      console.log(this.qeq);
    },

    reset() {
      this.name = "";
      this.load();
    },

    handleSizeChange(pageSize) {
      console.log(pageSize);
      this.pageSize = pageSize;
      this.load();
    },

    handleCurrentChange(pageNum) {
      console.log(pageNum);
      this.pageNum = pageNum;
      this.load();
    },

    handleFileUploadSuccess(res) {
      console.log(res);
      this.load();
    },

    download(url) {
      window.open(url);
    },

    onTreeNodeClick(node) {
      if (!node.isFile) {
        this.updateTableData(node);
        this.updateBreadcrumb(node);
      } else {
        this.tableData = [];
        this.tableData.push(node); // this.currentPath = this.breadcrumb.map(b => b);
      }
    },

    onRowDblClick(row) {
      if (!row.isFile) {
        const path = [...this.currentPath, row.name];
        this.navigateToPath(path);
      }
    },

    onBreadcrumbClick(index) {
      const path = this.currentPath.slice(0, index + 1);
      this.navigateToPath(path);
    },

    navigateToPath(path) {
      let currentNode = this.treeData[0];

      for (const part of path) {
        currentNode = currentNode.children.find(child => child.name === part);
      }

      this.updateTableData(currentNode);
      this.updateBreadcrumb(currentNode);
    },

    updateTableData(node) {
      this.tableData = node.children || [];
      this.currentPath = this.breadcrumb.map(b => b);
    },

    updateBreadcrumb(node) {
      const path = [];
      let current = node;

      while (current) {
        path.unshift(current.name);
        current = current.parent;
      }

      this.breadcrumb = path;
    },

    renderContent(h, {
      node,
      data,
      store
    }) {
      let type = data.type;
      let url = '';

      if (type === "folder") {
        if (node.data.isOpen) {
          url = "https://xiaoku-1312782209.cos.ap-guangzhou.myqcloud.com/xiaocool%2F%E5%9B%BE%E6%A0%87%2F%E6%96%87%E4%BB%B6%E5%A4%B9.svg";
        } else {
          url = "https://xiaoku-1312782209.cos.ap-guangzhou.myqcloud.com/xiaocool/%E5%9B%BE%E6%A0%87/3-%E6%96%87%E4%BB%B6%E5%A4%B9.svg";
        }
      } else {
        url = this.dynamicImageUrl(type);
      }

      return h('span', [h('img', {
        attrs: {
          src: url // 假设你已经在data中设置了正确的图片路径

        },
        style: {
          height: '16px',
          marginRight: '5px' // 设置右边距

        }
      }), h('span', node.label)]);
    },

    NodeExpandOpen(data, node, expanded) {
      // 如果节点被展开，更新 type 为打开的文件夹
      data.isOpen = true;
      console.log("展开了", data);
    },

    NodeExpandClose(data, node, expanded) {
      // 如果节点被关闭，更新 type 为关闭的文件夹
      data.isOpen = false;
      console.log("关闭了", data);
    },

    dynamicImageUrl(type) {
